import { Injectable } from '@angular/core';
import {ClipboardModule, Clipboard} from '@angular/cdk/clipboard';
import { compareTwoStrings } from 'string-similarity';

@Injectable({
  providedIn: 'root',
})
export class UtilitariosService {

  constructor(
    private clipboard: Clipboard
  ) { }

  primeraConMayusculas(field: string): string {
    field = field.toLowerCase();
    return field.charAt(0).toUpperCase() + field.slice(1);
  }

  reformatDate(dateStr: string): string {
    const dArr = dateStr.split('-');  // ex input "2010-01-18"
    return dArr[2] + '/' + dArr[1] + '/' + dArr[0]; // ex out: "18/01/10"
  }

  // optione el sistema operativo del cliente
  getOS() {
    const userAgent = window.navigator.userAgent;
    const platform = window.navigator.platform;
    const macosPlatforms = ['Macintosh', 'MacIntel', 'MacPPC', 'Mac68K'];
    const windowsPlatforms = ['Win32', 'Win64', 'Windows', 'WinCE'];
    const iosPlatforms = ['iPhone', 'iPad', 'iPod'];
    let os = null;

    if (macosPlatforms.indexOf(platform) !== -1) {
      os = 'Mac OS';
    } else if (iosPlatforms.indexOf(platform) !== -1) {
      os = 'iOS';
    } else if (windowsPlatforms.indexOf(platform) !== -1) {
      os = 'Windows';
    } else if (/Android/.test(userAgent)) {
      os = 'Android';
    } else if (!os && /Linux/.test(platform)) {
      os = 'Linux';
    }

    return os;
  }

  // remplazar caracteres especiales por ''
  addslashes(_string: string): string {
    if ( !_string ) {return; }
    return _string.replace(/\\/g, '').
        replace(/\u0008/g, '\\b').
        replace(/\t/g, '').
        replace(/\n/g, '').
        replace(/\f/g, '').
        replace(/\r/g, '').
        replace(/'/g, '').
        replace(/"/g, '');
  }

  // remplazar caracteres especiales por ''
  addslashes_space(_string: string): string {
    if ( !_string ) {return; }
    return _string.replace(/\\/g, ' ').
        replace(/\u0008/g, '\\b').
        replace(/\t/g, '').
        replace(/\n/g, ' ').
        replace(/\f/g, '').
        replace(/\r/g, ' ').
        replace(/'/g, '').
        replace(/"/g, '');
  }

  // compartir data titulo y url
  sharedNative(urlCartaVirtual: string , nomSede: string): any {
    // verificar si es telfono
    const _oSUs = this.getOS();
    if ( _oSUs === 'Windows' ||  _oSUs === 'Mac OS') {
      this.clipboard.copy(urlCartaVirtual);
      return;
    }

    if (navigator.share) {
      navigator.share({
        title: 'Carta Virutal ' + nomSede,
        url: urlCartaVirtual
      }).then(() => {
        return {mensaje: 'Copiado'};
      })
      .catch(console.error);
    } else {
      // fallback
    }
  }

  // reproduce sonido bepp
  beep() {
    // tslint:disable-next-line:max-line-length
    const snd = new  Audio('data:audio/wav;base64,');
    snd.play();
  }

  async delay(ms: number) {
    return await new Promise( resolve => setTimeout(resolve, ms) );
  }

  compareSimilaryText(s1: string, s2: string) {
    return compareTwoStrings(s1, s2);
  }

  isBetweenHoursNow(h1: string, h2: string): boolean {
    const dateNow = new Date();
    // const dNow = new Date();
    const dIni = new Date();
    const dFin = new Date();
    const horaIni = h1.split(':');
    const horaFin = h2.split(':');
    const isHoursDayAfter = parseFloat(horaIni[0]) > parseFloat(horaFin[0]);
    // const isHoursDayNow = dateNow.getHours() > parseFloat(horaFin[0]);



    dIni.setHours(parseFloat(horaIni[0]));
    dIni.setMinutes(parseFloat(horaIni[1]));

    dFin.setHours(parseFloat(horaFin[0]));
    dFin.setMinutes(parseFloat(horaFin[1]));

    if ( isHoursDayAfter ) { //  si la hora incial es mayor entonces hfinal pasa de las 00 horas
      // dIni.setDate(dIni.getDate() - 1);
      if ( dateNow.getHours() >  dFin.getHours() ) {
        dFin.setDate(dFin.getDate() + 1);
      } else {
        dIni.setDate(dIni.getDate() - 1);
      }
    }

    return dateNow.getTime() >= dIni.getTime() && dateNow.getTime() <= dFin.getTime();
  }


  // tiempo transcurrido con la hora actual en minutos
  getTimeMinutesDiff(_dateCal: string) {
    const h1: any = new Date(_dateCal);
    const h2: any = new Date();
    return new Date(h2 - h1).getMinutes();
  }

  arrGroupBy(xs: any, key: string) {
    return xs.reduce(function(rv, x) {
      (rv[x[key]] = rv[x[key]] || []).push(x);
      return rv;
    }, {});
  }


  roundAmount(amount: number): number {
  // Extraer la parte entera y decimal del nÃºmero
  const integerPart = Math.floor(amount);
  const decimalPart = amount - integerPart;

  // Redondear hacia abajo si el decimal es menor a 0.50
  if (decimalPart < 0.50) {
    return integerPart;
  } else {
    // Redondear hacia arriba si el decimal es 0.50 o mayor
    return integerPart + 0.50;
  }
}


}
